    overflow: hidden; /**/
 }
 /*Safari,Opera,IE*/
 .text-overflow span{
   display: block;
   width: 100px; /**/
   overflow: hidden;/**/
   white-space: nowrap;/**/
   -o-text-overflow: ellipsis;/*Operaellipsis*/
   text-overflow: ellipsis;/*SafariIEellipsis*/
 }

 /*Firefox*/
 @-moz-document url-prefix(){ /*@-moz-document url-perfix(){}firefoxfirefoxhack*/
