// YUI get method
YAHOO.util.Dom.get = function (el) {
    if (YAHOO.lang.isString(el)) {
        return document.getElementById(el);
    }

    if (YAHOO.lang.isArray(el)) {
        var c = [];
        for (var i = 0, len = el.length; i < len; ++i) {
            c[c.length] = YAHOO.util.Dom.get(el[i]);
        }
        return c;
    }

    if (el) {
        return el;
    }

    return null;
};

